package com.DataTypePackage;

public class DataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//	integer datatype
	int x = 1; 
	int y = 3;
	int j = 3; 
	x = 5;
	
	//double datatype
	double a = 1.5;
	double d = 2.5;
	
	//character datatype
	char z = 'b';
	
	//String a non primitive data type. String is a class.
	String name = "Shomir";
	
	//boolean datatype
	
	boolean b = true; 
	boolean c = false;
	
	
	
	System.out.println("My name is " + name);
	}  
	
	

}
